package io.geier

class Results {
    public static void main(String[] args) {
        List<String> emptyList = [];
        List<String> nullList = null;

        if (emptyList == null || emptyList.empty) {
            println "We have empty list"
        }

        if (emptyList?.empty) {
            println "Empty list indeed"
        }

        println "-----------------"

        if (nullList == null || nullList.empty) {
            println "Checked empty list"
        }

        if (nullList?.empty) {
            println "We do have empty list"
        }
    }
}

